import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchTodos, addTodo, updateTodo, deleteTodo } from '../features/todoSlice';
import { useForm } from 'react-hook-form';

const TodoList = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.todos);

  const [isEditing, setIsEditing] = useState(null);
  const [editValue, setEditValue] = useState('');

  const { register, handleSubmit, reset } = useForm();

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const handleAddTodo = (data) => {
    dispatch(addTodo({ title: data.title }));
    reset();
  };

  const handleUpdateTodo = (id) => {
    dispatch(updateTodo({ id, title: editValue }));
    setIsEditing(null);
  };

  const handleToggleComplete = (id) => {
    dispatch(toggleComplete(id));
  };

  return (
    <div>
      <form onSubmit={handleSubmit(handleAddTodo)}>
        <input
          type="text"
          {...register('title', { required: true })}
          placeholder="Add a new task"
        />
        <button type="submit">Add Todo</button>
      </form>

      <ul>
        {todos.map((todo) => (
          <li key={todo._id} className={todo.completed ? 'completed' : ''}>
            {isEditing === todo._id ? (
              <>
                <input
                  type="text"
                  value={editValue}
                  onChange={(e) => setEditValue(e.target.value)}
                />
                <button onClick={() => handleUpdateTodo(todo._id)}>Save</button>
                <button onClick={() => setIsEditing(null)}>Cancel</button>
              </>
            ) : (
              <>
                <span>{todo.title}</span>
                <button onClick={() => setIsEditing(todo._id) || setEditValue(todo.title)}>
                  Edit
                </button>

                <button onClick={() => dispatch(deleteTodo(todo._id))}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
