import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchTodos, deleteTodo, updateTodo } from '../features/todoSlice';

const TodoList = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.todos);

  const [isEditing, setIsEditing] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  if (!Array.isArray(todos)) {
    return <p>Loading...</p>;
  }

  const handleEdit = (id, title) => {
    setIsEditing(id);
    setEditValue(title);
    setError('');
  };

  const handleSave = (id) => {
    if (editValue.trim().length < 3) {
      setError('Title must be at least 3 characters long.');
      return;
    }

    dispatch(updateTodo({ id, title: editValue.trim() }));
    setIsEditing(null);
    setEditValue('');
  };

  return (
    <ul>
      {todos.map((todo) => (
        <li key={todo._id}>
          {isEditing === todo._id ? (
            <>
              <input
                type="text"
                value={editValue}
                onChange={(e) => setEditValue(e.target.value)}
              />
              {error && <p style={{ color: 'red' }}>{error}</p>}
              <button onClick={() => handleSave(todo._id)}>Save</button>
              <button onClick={() => setIsEditing(null)}>Cancel</button>
            </>
          ) : (
            <>
              <span>{todo.title}</span>
              <button onClick={() => handleEdit(todo._id, todo.title)}>Edit</button>
              <button onClick={() => dispatch(deleteTodo(todo._id))}>Delete</button>
            </>
          )}
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
