import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchTodos, deleteTodo, updateTodo } from '../features/todoSlice'; 

const TodoList = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.todos);

  const [isEditing, setIsEditing] = useState(null);
  const [editValue, setEditValue] = useState('');

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const handleUpdate = (id) => {
    if (editValue.trim()) {
      dispatch(updateTodo({ id, title: editValue }));
      setIsEditing(null);
      setEditValue('');
    }
  };

  return (
    <ul>
      {todos.map((todo) => (
        <li key={todo._id}>
          {isEditing === todo._id ? (
            <>
              <input
                type="text"
                value={editValue}
                onChange={(e) => setEditValue(e.target.value)}
              />
              <button onClick={() => handleUpdate(todo._id)}>Save</button>
              <button onClick={() => setIsEditing(null)}>Cancel</button>
            </>
          ) : (
            <>
              <span>{todo.title}</span>
              <button onClick={() => {
                setIsEditing(todo._id);
                setEditValue(todo.title);
              }}>Edit</button>
              <button onClick={() => dispatch(deleteTodo(todo._id))}>Delete</button>
            </>
          )}
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
